            RX += (GammaLUT_I[BmpBuf_[P        + 2]]) * OpMatrixIR_[2];  GX += (GammaLUT_I[BmpBuf_[P        + 1]]) * OpMatrixIG_[2];  BX += (GammaLUT_I[BmpBuf_[P        + 0]]) * OpMatrixIB_[2];
            RX += (GammaLUT_I[BmpBuf_[P + 4    + 2]]) * OpMatrixIR_[3];  GX += (GammaLUT_I[BmpBuf_[P + 4    + 1]]) * OpMatrixIG_[3];  BX += (GammaLUT_I[BmpBuf_[P + 4    + 0]]) * OpMatrixIB_[3];
            RX += (GammaLUT_I[BmpBuf_[P + 8    + 2]]) * OpMatrixIR_[4];  GX += (GammaLUT_I[BmpBuf_[P + 8    + 1]]) * OpMatrixIG_[4];  BX += (GammaLUT_I[BmpBuf_[P + 8    + 0]]) * OpMatrixIB_[4];
            RX += (GammaLUT_I[BmpBuf_[P - 8    + 2]]) * OpMatrixIR_[5];  GX += (GammaLUT_I[BmpBuf_[P - 8    + 1]]) * OpMatrixIG_[5];  BX += (GammaLUT_I[BmpBuf_[P - 8    + 0]]) * OpMatrixIB_[5];
            RX += (GammaLUT_I[BmpBuf_[P - 4    + 2]]) * OpMatrixIR_[6];  GX += (GammaLUT_I[BmpBuf_[P - 4    + 1]]) * OpMatrixIG_[6];  BX += (GammaLUT_I[BmpBuf_[P - 4    + 0]]) * OpMatrixIB_[6];
            RX += (GammaLUT_I[BmpBuf_[P        + 2]]) * OpMatrixIR_[7];  GX += (GammaLUT_I[BmpBuf_[P        + 1]]) * OpMatrixIG_[7];  BX += (GammaLUT_I[BmpBuf_[P        + 0]]) * OpMatrixIB_[7];
            RX += (GammaLUT_I[BmpBuf_[P + 4    + 2]]) * OpMatrixIR_[8];  GX += (GammaLUT_I[BmpBuf_[P + 4    + 1]]) * OpMatrixIG_[8];  BX += (GammaLUT_I[BmpBuf_[P + 4    + 0]]) * OpMatrixIB_[8];
            RX += (GammaLUT_I[BmpBuf_[P + 8    + 2]]) * OpMatrixIR_[9];  GX += (GammaLUT_I[BmpBuf_[P + 8    + 1]]) * OpMatrixIG_[9];  BX += (GammaLUT_I[BmpBuf_[P + 8    + 0]]) * OpMatrixIB_[9];
            RX += (GammaLUT_I[BmpBuf_[P - 8    + 2]]) * OpMatrixIR_[10]; GX += (GammaLUT_I[BmpBuf_[P - 8    + 1]]) * OpMatrixIG_[10]; BX += (GammaLUT_I[BmpBuf_[P - 8    + 0]]) * OpMatrixIB_[10];
            RX += (GammaLUT_I[BmpBuf_[P - 4    + 2]]) * OpMatrixIR_[11]; GX += (GammaLUT_I[BmpBuf_[P - 4    + 1]]) * OpMatrixIG_[11]; BX += (GammaLUT_I[BmpBuf_[P - 4    + 0]]) * OpMatrixIB_[11];
            RX += (GammaLUT_I[BmpBuf_[P        + 2]]) * OpMatrixIR_[12]; GX += (GammaLUT_I[BmpBuf_[P        + 1]]) * OpMatrixIG_[12]; BX += (GammaLUT_I[BmpBuf_[P        + 0]]) * OpMatrixIB_[12];
            RX += (GammaLUT_I[BmpBuf_[P + 4    + 2]]) * OpMatrixIR_[13]; GX += (GammaLUT_I[BmpBuf_[P + 4    + 1]]) * OpMatrixIG_[13]; BX += (GammaLUT_I[BmpBuf_[P + 4    + 0]]) * OpMatrixIB_[13];
            RX += (GammaLUT_I[BmpBuf_[P + 8    + 2]]) * OpMatrixIR_[14]; GX += (GammaLUT_I[BmpBuf_[P + 8    + 1]]) * OpMatrixIG_[14]; BX += (GammaLUT_I[BmpBuf_[P + 8    + 0]]) * OpMatrixIB_[14];
            RX += (GammaLUT_I[BmpBuf_[P + L1m8 + 2]]) * OpMatrixIR_[15]; GX += (GammaLUT_I[BmpBuf_[P + L1m8 + 1]]) * OpMatrixIG_[15]; BX += (GammaLUT_I[BmpBuf_[P + L1m8 + 0]]) * OpMatrixIB_[15];
            RX += (GammaLUT_I[BmpBuf_[P + L1m4 + 2]]) * OpMatrixIR_[16]; GX += (GammaLUT_I[BmpBuf_[P + L1m4 + 1]]) * OpMatrixIG_[16]; BX += (GammaLUT_I[BmpBuf_[P + L1m4 + 0]]) * OpMatrixIB_[16];
            RX += (GammaLUT_I[BmpBuf_[P + L1   + 2]]) * OpMatrixIR_[17]; GX += (GammaLUT_I[BmpBuf_[P + L1   + 1]]) * OpMatrixIG_[17]; BX += (GammaLUT_I[BmpBuf_[P + L1   + 0]]) * OpMatrixIB_[17];
            RX += (GammaLUT_I[BmpBuf_[P + L1p4 + 2]]) * OpMatrixIR_[18]; GX += (GammaLUT_I[BmpBuf_[P + L1p4 + 1]]) * OpMatrixIG_[18]; BX += (GammaLUT_I[BmpBuf_[P + L1p4 + 0]]) * OpMatrixIB_[18];
            RX += (GammaLUT_I[BmpBuf_[P + L1p8 + 2]]) * OpMatrixIR_[19]; GX += (GammaLUT_I[BmpBuf_[P + L1p8 + 1]]) * OpMatrixIG_[19]; BX += (GammaLUT_I[BmpBuf_[P + L1p8 + 0]]) * OpMatrixIB_[19];
            RX += (GammaLUT_I[BmpBuf_[P + L2m8 + 2]]) * OpMatrixIR_[20]; GX += (GammaLUT_I[BmpBuf_[P + L2m8 + 1]]) * OpMatrixIG_[20]; BX += (GammaLUT_I[BmpBuf_[P + L2m8 + 0]]) * OpMatrixIB_[20];
            RX += (GammaLUT_I[BmpBuf_[P + L2m4 + 2]]) * OpMatrixIR_[21]; GX += (GammaLUT_I[BmpBuf_[P + L2m4 + 1]]) * OpMatrixIG_[21]; BX += (GammaLUT_I[BmpBuf_[P + L2m4 + 0]]) * OpMatrixIB_[21];
            RX += (GammaLUT_I[BmpBuf_[P + L2   + 2]]) * OpMatrixIR_[22]; GX += (GammaLUT_I[BmpBuf_[P + L2   + 1]]) * OpMatrixIG_[22]; BX += (GammaLUT_I[BmpBuf_[P + L2   + 0]]) * OpMatrixIB_[22];
            RX += (GammaLUT_I[BmpBuf_[P + L2p4 + 2]]) * OpMatrixIR_[23]; GX += (GammaLUT_I[BmpBuf_[P + L2p4 + 1]]) * OpMatrixIG_[23]; BX += (GammaLUT_I[BmpBuf_[P + L2p4 + 0]]) * OpMatrixIB_[23];
