        RX = 0;
        GX = 0;
        BX = 0;
        RX += (GammaLUT_I[BmpBuf_[P - 8    + 2]]) * OpMatrixIR_[0];  GX += (GammaLUT_I[BmpBuf_[P - 8    + 1]]) * OpMatrixIG_[0];  BX += (GammaLUT_I[BmpBuf_[P - 8    + 0]]) * OpMatrixIB_[0];
